CREATE TABLE IF NOT EXISTS PRODUCT_ORDER(ID uuid DEFAULT gen_random_uuid() PRIMARY KEY, PROFILE_ID uuid NOT NULL, CREDIT_CARD_NUMBER VARCHAR(255) NOT NULL, SHIPPING_ADDRESS VARCHAR(255) NOT NULL);
CREATE TABLE IF NOT EXISTS PRODUCT_ORDER_ITEM(ID uuid DEFAULT gen_random_uuid() PRIMARY KEY, PRODUCT_ORDER_ID uuid NOT NULL, PRODUCT_ID uuid NOT NULL, CONSTRAINT product_in_order_unique_constraint UNIQUE (PRODUCT_ORDER_ID, PRODUCT_ID));